import classes from './App.module.css';

const time = new Date().getHours();
var headingContent = '';

if(time < 12 ){
	headingContent = 'Good Morning';
	inline={color:'red'};

}
else if(time > 12  && time < 18){
	headingContent = 'Good Afternoon';
	inline={color:'green'}
}
else{
		headingContent = 'Good Evening';
	inline={color:'blue'}
}

function App() {
	return(
		<div>
			<h1 className={classes.heading} style={inline}>{headingContent}</h1>
		</div>
	);
}

export default App;
